VALUES(#{author}
    ,#{regDate}
    ,#{marked}
    ,#{title}
    ,#{content}
    ,#{region})

mapper.update:
UPDATE member_info.board
SET marked = #{marked},
    title = #{title},
    content = #{content}
WHERE id = #{id} AND region = #{region}
